import React, { useState, ChangeEvent, FormEvent } from 'react';
import { Container, Box, TextField, Button, Typography } from '@mui/material';

interface RegistrationFormData {
  password: string;
  first_name: string;
  last_name: string;
  age: number;
}

const SingUp: React.FC = () => {
  const [formData, setFormData] = useState<RegistrationFormData>({
    password: '',
    first_name: '',
    last_name: '',
    age: 0,
  });

  const [message, setMessage] = useState<string>('');

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'age' ? Number(value) : value,
    }));
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log('Submitted data:', formData);

    // Приклад відправки даних на сервер
    try {
      const response = await fetch('http://your-backend-url/register/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        setMessage('Реєстрація пройшла успішно!');
      } else {
        const errorData = await response.json();
        setMessage(`Помилка: ${errorData.error}`);
      }
    } catch (error) {
      setMessage('Сталася помилка при відправці даних.');
      console.error('Помилка:', error);
    }
  };

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          marginTop: 4,
          padding: 3,
          boxShadow: 3,
          borderRadius: 2,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          backgroundColor: '#fff',
        }}
      >
        <Typography variant="h5" component="h1" sx={{ mb: 2 }}>
          Реєстрація
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="first_name"
            name="first_name"
            label="Ім'я"
            value={formData.first_name}
            onChange={handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            id="last_name"
            name="last_name"
            label="Прізвище"
            value={formData.last_name}
            onChange={handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            id="password"
            name="password"
            label="Пароль"
            type="password"
            value={formData.password}
            onChange={handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            id="age"
            name="age"
            label="Вік"
            type="number"
            value={formData.age}
            onChange={handleChange}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            sx={{ mt: 3 }}
          >
            Зареєструватися
          </Button>
          {message && (
            <Typography variant="body2" color="success.main" sx={{ mt: 2 }}>
              {message}
            </Typography>
          )}
        </Box>
      </Box>
    </Container>
  );
};

export default SingUp;
